package FullProgramme;

public class AllDetailsOfStudent {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1.Variable Declaration
		
		String Name;
		int RollNum;
		long PhoneNumber;
		byte age;
		String gender;
		char Grade;
		float height;
		float weight;
		Boolean result;
		char bloodgroup;
		
		
		//2.Variable Assign
		
		Name="Rohan Shashikant Patil";
		RollNum=1258;
		PhoneNumber= 8657086238L;
		age=27;
		gender="M";
		Grade='A';
		height=165.5f;
		weight=58.785256f;
		result=true;
		bloodgroup='A';
		
		//3.Usages
		
		System.out.println("My Name is "+Name);
		System.out.println("My Roll Number from decending order is "+RollNum);
		System.out.println("My phone number is "+PhoneNumber);
		System.out.println("My age as on date is "+age);
		System.out.println("My gender is "+gender);
		System.out.println("My grade is "+Grade);
		System.out.println("My height is "+height);
		System.out.println("My weight is "+weight);
		System.out.println("My Final result is "+result);
		System.out.println("My blood group is "+bloodgroup);
		
		
		
		
		
		
		
		

	}

}
